import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      jsonPokemon: '',
      pokemonName: []
    };
    this.searchPokemon = this.searchPokemon.bind(this);
  }

  callApi() {
    // Call the API page
    fetch('https://pokeapi.co/api/v2/pokemon?limit=1000').then((result) => {
      // Get the result
      // If we want text, call result.text()
      return result.json();
    }).then((jsonResult) => {
      // Do something with the result
      this.setState({ jsonPokemon: jsonResult });
      console.log(jsonResult);
    })
  }

  searchPokemon(event) {
    if(this.state.jsonPokemon === '') {
      this.callApi();
    }
    let pokemon = event.target.value;
    if(pokemon.length >= 2) {
      console.log(this.state.jsonPokemon);
      for(var i = 0; i < this.state.jsonPokemon.results.length - 1; i++) {
        console.log(this.state.jsonPokemon.results[i].name);
        this.setState(state => ( state.pokemonName.push(state.jsonPokemon.results[i].name)));

      }
    }
    //this.setState({ changedValue: event.target.value });
  }

  render() {
    return (
      <div className="App">
        <form className="SearchForm">
          Pokemon: <input type="text" className="InputForm" placeholder="Chercher votre pokÃ©mon..." onChange={ this.searchPokemon }/>
        </form>
        <ul>
          {this.state.pokemonName.map(item =>
              <li> {item} </li>
          )}
        </ul>
        {/* {pokemontList} */}
      </div>
    );
  }
}

export default App;
