import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      jsonPokemon: '',
      listPokemon: [],
      listPokemonDetails: []
    };
    this.searchPokemon = this.searchPokemon.bind(this);
    this.detailsThisPokemon = this.detailsThisPokemon.bind(this);
  }

  callAllPokemon() {
    // Call the API page
    fetch('https://pokeapi.co/api/v2/pokemon?limit=1000').then((result) => {
      // Get the result
      // If we want text, call result.text()
      return result.json();
    }).then((jsonResult) => {
      // Do something with the result
      this.setState({ jsonPokemon: jsonResult });
    })
  }

  callPokemonDetails(url, name) {
    // Call the API page
    fetch(url).then((result) => {
      // Get the result
      // If we want text, call result.text()
      return result.json();
    }).then((jsonPokemon) => {
      // Do something with the result
      let pokemonDetails = [];
      let type = [];
      let baseStats = [];
      let typeStats = [];
      let averageTypesStats= [];

      //Call all types of this pokemon
      for(let i = 0; i < jsonPokemon.types.length; i++) {
        fetch(jsonPokemon.types[i].type.url).then((result) => {
          return result.json();
        }).then((jsonResult) => {
          //Call all pokemons details of this type
          for(let x = 0; x < jsonResult.pokemon.length; x++) {
            let stats = [];
            fetch(jsonResult.pokemon[i].pokemon.url).then((result) => {
              return result.json();
            }).then((jsonOtherPokemon) => {
              for(let y = 0; y < jsonOtherPokemon.stats.length; y++) {
                stats.push({name: jsonOtherPokemon.stats[y].stat.name, value: jsonOtherPokemon.stats[y].base_stat});             
              }
            })
            typeStats.push(stats);
          }
          averageTypesStats.push(this.averageTypeStats(typeStats));
        })
        type.push({name: jsonPokemon.types[i].type.name, stats: averageTypesStats});
      }

      // write each stats in array
      for(var x = 0; x < jsonPokemon.stats.length; x++) {
        baseStats.push({name: jsonPokemon.stats[x].stat.name, value: jsonPokemon.stats[x].base_stat});
      }

      // Push all data in array
      pokemonDetails.push(name, type, jsonPokemon.sprites.front_default, baseStats)
      this.setState({ listPokemonDetails: pokemonDetails });
    })
  }

  averageTypeStats(arrayStats) {
    let averageStats = [];
    let nbPokemon = arrayStats.length;
    let hp = 0;
    let attack = 0;
    let speed = 0;
    let defense = 0;
    let special_attack = 0;
    let special_defense = 0;
    for(var i = 0; i < nbPokemon; i++) {
    console.log(arrayStats[i]);
      for(var x = 0; x < arrayStats[i].length; x++) {
    console.log("test");

        var name = arrayStats[i][x].name;
        if(name === 'hp')
          console.log(arrayStats[i][x].value)
        else if(name === 'attack')
          attack += arrayStats[i][x].value;
        else if(name === 'speed')
          speed += arrayStats[i][x].value;
        else if(name === 'defense')
        defense += arrayStats[i][x].value;
        else if(name === 'special-attack')
        special_attack += arrayStats[i][x].value;
        else if(name === 'special-defense')
        special_defense += arrayStats[i][x].value;
      }
    }
    averageStats.push({name: 'hp', value: hp/nbPokemon }, {name: 'attack', value:attack/nbPokemon}, {name: 'speed', value: speed/nbPokemon}, {name: 'defense', value: defense/nbPokemon }, {name: 'special-attack', value: special_attack/nbPokemon }, {name: 'special-defense', value: special_defense/nbPokemon});
    console.log(averageStats)
    return averageStats;
  }

  searchPokemon(event) {
    if(this.state.jsonPokemon === '') {
      this.callAllPokemon();
    }
    const pokemon = event.target.value;
    if(pokemon.length >= 2) {
      let newArrayPokemon = [];
      for(var i = 0; i < this.state.jsonPokemon.results.length; i++) {
        if((this.state.jsonPokemon.results[i].name).includes(pokemon))
          newArrayPokemon.push(this.state.jsonPokemon.results[i]);
      }
      this.setState({ listPokemon: newArrayPokemon });
    }
  }

  detailsThisPokemon(event) {
    this.callPokemonDetails(event.currentTarget.dataset.url, event.currentTarget.dataset.name);
  }

  render() {
    return (
      <div className="App">
        <form className="SearchForm">
          Pokemon: <input type="text" className="InputForm" placeholder="Chercher votre pokémon..." onChange={ this.searchPokemon }/>
        </form>
        <ul className="PokemonList">
          { this.state.listPokemon.map(item => <li key={ item.name } className="PokemonName" onClick={this.detailsThisPokemon} data-name={item.name} data-url={item.url}> {item.name} </li>) }
        </ul>
        { this.state.listPokemonDetails.length > 0 &&
          <div className="PokemonDetails">
            <div className="PokemonImage">
              <img src={this.state.listPokemonDetails[2]} alt="Indisponible"/>
            </div>
            <div className="PokemonInfos">
              <div className="Name">
                { this.state.listPokemonDetails[0] }
              </div>
              <div className="Types">
                Type(s): 
                <ul className="UlTypes">
                  { this.state.listPokemonDetails[1].map(item => <li key={ item.name }> {item.name} </li>) }
                </ul>
              </div>
            </div>
            <div className="PokemonStats">
              Stats de base:
              <ul className="UlBaseStats">
                { this.state.listPokemonDetails[3].map(item => <li key={ item.name }> {item.name + ': ' + item.value} </li>) }
              </ul>
                { this.state.listPokemonDetails[1].map((type,index) => <ul key={type.name} className="UlTypeStats"> Moyenne du type {type.name} : {type.stats.map( stats => <li key={type.name+'_'+stats.name}> {stats.name + ': ' + stats.value}</li> )} </ul>) }
            </div>
          </div>
        }
      </div>
    );
  }
}

export default App;
