import React from 'react';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      jsonPokemon: '',
      listPokemon: []
    };
    this.searchPokemon = this.searchPokemon.bind(this);
  }

  callApi() {
    // Call the API page
    fetch('https://pokeapi.co/api/v2/pokemon?limit=1000').then((result) => {
      // Get the result
      // If we want text, call result.text()
      return result.json();
    }).then((jsonResult) => {
      // Do something with the result
      this.setState({ jsonPokemon: jsonResult });
      console.log(jsonResult);
    })
  }

  searchPokemon(event) {
    if(this.state.jsonPokemon === '') {
      this.callApi();
    }
    let pokemon = event.target.value;
    if(pokemon.length >= 2) {
      let newArrayPokemon = [];
      for(var i = 0; i < this.state.jsonPokemon.results.length; i++) {
        newArrayPokemon.push(this.state.jsonPokemon.results[i]);
      }
      this.setState({ listPokemon: newArrayPokemon });
      console.log(this.state.listPokemon);
    }
  }

  render() {
    const test = this.state.listPokemon.map((element, index) => <li value={index+1}>{element}</li>);
